css:
/* Full-page layout */
body {
    margin: 0;
    padding: 0;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100vh;
    background-color: #f4f7fc;
    font-family: Arial, sans-serif;
}

/* Branding Header */
.brand-header {
    text-align: center;
    background-color: #007bff;
    color: white;
    width: 100%;
    padding: 15px 0;
    position: absolute;
    top: 0;
    left: 0;
}


/* Receipt Box */
.receipt-container {
    background: white;
    padding: 25px;
    width: 100%;
    max-width: 1100px; /* Increased width */
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
    border-radius: 8px;
    text-align: left;
    margin-top: 20px;
}

/* Header */
.receipt-header {
    text-align: center;
    font-size: 22px;
    font-weight: bold;
    color: #007bff;
    border-bottom: 2px solid #007bff;
    padding-bottom: 10px;
    margin-bottom: 30px;
}

/* Receipt Content */
.receipt-content p {
    font-size: 18px;
    margin: 10px 0;
}

.receipt-content strong {
    font-weight: bold;
}

/* Signature */
.signature {
    text-align: right;
    margin-top: 20px;
    color:  #007bff;
}

.signature .line {
    width: 150px;
    height: 2px;
    background: black;
    margin: 5px 0 10px auto;
    color: ;
}

/* Footer */
.receipt-footer {
    text-align: center;
    font-size: 14px;
    color: #555;
    margin-top: 15px;
}

.receipt-footer a {
    color: #007bff;
    text-decoration: none;
}
